import * as React from "react";
import { LocalizationProvider } from "@mui/x-date-pickers/LocalizationProvider";
import { AdapterDayjs } from "@mui/x-date-pickers/AdapterDayjs";
import { DateCalendar } from "@mui/x-date-pickers/DateCalendar";
import { Box, Button, Typography } from "@mui/material";
import dayjs, { Dayjs } from "dayjs"; // Ensure dayjs is installed

export default function StyledDateCalendar() {
  const [currentDate, setCurrentDate] = React.useState<Dayjs>(dayjs()); // State for tracking the displayed month and year

  const handleMonthChange = (newDate: Dayjs) => {
    setCurrentDate(newDate); // Update state when the user navigates to a new month
  };

  const handleCancel = () => {
    console.log("Cancel clicked");
  };

  const handleOk = () => {
    console.log("OK clicked");
  };

  return (
    <LocalizationProvider dateAdapter={AdapterDayjs}>
      <Box
        sx={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
          padding: 3,
          border: "1px solid #ccc",
          borderRadius: 4,
          maxWidth: 450,
          margin: "0 auto",
          backgroundColor: "#fdfdfd",
          boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
        }}
      >
        {/* Header */}
        <Typography
          variant="h4"
          sx={{
            fontFamily: "Cormorant Garamond, serif",
            fontWeight: "bold",
            marginBottom: 1,
            color: "#333",
          }}
        >
          {currentDate.format("YYYY")} Calendar
        </Typography>
        <Typography
          variant="h5"
          sx={{
            fontFamily: "Cormorant Garamond, serif",
            fontWeight: "400",
            marginBottom: 3,
            color: "#555",
          }}
        >
          {currentDate.format("MMMM YYYY")}
        </Typography>

        {/* Calendar */}
        <Box
          sx={{
            padding: 2,
            border: "1px solid #ddd",
            borderRadius: 4,
            backgroundColor: "#fff",
          }}
        >
          <DateCalendar
            onMonthChange={handleMonthChange} // Update displayed month when navigation arrows are used
            defaultValue={currentDate} // Set the initial calendar month
          />
        </Box>

        {/* Buttons */}
        <Box
          sx={{
            display: "flex",
            justifyContent: "space-between",
            marginTop: 3,
            width: "100%",
          }}
        >
          <Button
            variant="outlined"
            color="secondary"
            onClick={handleCancel}
            sx={{
              fontFamily: "Roboto, sans-serif",
              textTransform: "none",
              padding: "6px 16px",
            }}
          >
            Cancel
          </Button>
          <Button
            variant="contained"
            color="primary"
            onClick={handleOk}
            sx={{
              fontFamily: "Roboto, sans-serif",
              textTransform: "none",
              padding: "6px 16px",
            }}
          >
            OK
          </Button>
        </Box>
      </Box>
    </LocalizationProvider>
  );
}
