import * as React from "react";
import { Box, Typography, Card, CardContent, CardMedia } from "@mui/material";

export default function VenuesPage() {
  // Array of venues, each with a name, description, and image URL (replace with real data)
  const venues = [
    { name: "Venue A", description: "A beautiful venue with a scenic view.", image: "/path/to/imageA.jpg" },
    { name: "Venue B", description: "An elegant venue in the city center.", image: "/path/to/imageB.jpg" },
    { name: "Venue C", description: "Perfect for intimate gatherings.", image: "/path/to/imageC.jpg" },
    { name: "Venue D", description: "A spacious venue for large events.", image: "/path/to/imageD.jpg" },
    { name: "Venue E", description: "A modern venue with high-tech amenities.", image: "/path/to/imageE.jpg" },
    { name: "Venue F", description: "A cozy venue with a rustic vibe.", image: "/path/to/imageF.jpg" },
    { name: "Venue G", description: "Ideal for outdoor events with great views.", image: "/path/to/imageG.jpg" },
    { name: "Venue H", description: "A historical venue with classic charm.", image: "/path/to/imageH.jpg" },
    { name: "Venue I", description: "An artsy venue with creative spaces.", image: "/path/to/imageI.jpg" },
    { name: "Venue J", description: "A venue with a luxurious feel and great service.", image: "/path/to/imageJ.jpg" },
    { name: "Venue K", description: "A venue with a modern yet intimate setting.", image: "/path/to/imageK.jpg" },
    { name: "Venue L", description: "A venue with an urban aesthetic.", image: "/path/to/imageL.jpg" },
    { name: "Venue M", description: "A beautiful outdoor venue surrounded by nature.", image: "/path/to/imageM.jpg" },
    { name: "Venue N", description: "A versatile venue with flexible event spaces.", image: "/path/to/imageN.jpg" },
    { name: "Venue O", description: "A venue with cutting-edge technology and style.", image: "/path/to/imageO.jpg" },
  ];

  return (
    <>
      {/* Grid Layout of 15 Venues */}
      <Box
        sx={{
          display: "grid",
          gridTemplateColumns: "repeat(3, 1fr)", // 3 columns of equal width
          gridTemplateRows: "repeat(5, 1fr)", // 5 rows
          gap: 2, // Adds space between the boxes
          marginTop: 2,
        }}
      >
        {/* Loop through the venues array to display each venue */}
        {venues.map((venue, index) => (
          <Box
            key={index}
            sx={{
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
              justifyContent: "center",
              border: "1px solid #ccc",
              borderRadius: 2,
              boxShadow: 3,
              aspectRatio: "1", // Ensures the box is square
            }}
          >
            <Card sx={{ width: "100%", height: "100%" }}>
              {/* Image (replace with a real image path) */}
              <CardMedia
                component="img"
                height="140"
                image={venue.image} // Set the image path
                alt={venue.name}
              />
              <CardContent>
                <Typography variant="h6" sx={{ fontWeight: "bold", textAlign: "center" }}>
                  {venue.name}
                </Typography>
                <Typography variant="body2" sx={{ textAlign: "center", color: "#555" }}>
                  {venue.description}
                </Typography>
              </CardContent>
            </Card>
          </Box>
        ))}
      </Box>
    </>
  );
}


