import * as React from "react";
import { Box, Typography, Button } from "@mui/material";
import NavigationBar from "./NavigationBar";
import DateRangeIcon from "@mui/icons-material/DateRange";
import { useNavigate } from "react-router-dom";

export default function NewPage() {
  const navigate = useNavigate();

  // Get the selected date from localStorage
  const [selectedDate, setSelectedDate] = React.useState(
    localStorage.getItem("selectedDate") || "Select a date"
  );

  // State to manage the active section
  const [activeSection, setActiveSection] = React.useState("default");

  const handleChangeDate = () => {
    navigate("/calendar"); // Navigate to the calendar page
  };

  // Function to render content dynamically based on activeSection
  const renderContent = () => {
    switch (activeSection) {
      case "venues":
        return <Typography variant="h4">Welcome to the Venues Section</Typography>;
      case "hair":
        return <Typography variant="h4">Welcome to the Hair Section</Typography>;
      case "makeup":
        return <Typography variant="h4">Welcome to the Makeup Section</Typography>;
      case "photographers":
        return <Typography variant="h4">Welcome to the Photographers Section</Typography>;
      case "music":
        return <Typography variant="h4">Welcome to the Music Section</Typography>;
      case "florist":
        return <Typography variant="h4">Welcome to the Florist Section</Typography>;
      case "dresser":
        return <Typography variant="h4">Welcome to the Dresser Section</Typography>;
      case "planner":
        return <Typography variant="h4">Welcome to the Planner Section</Typography>;
      default:
        return (
          <Typography variant="h4">
            Select a category from the navigation bar to get started!
          </Typography>
        );
    }
  };

  return (
    <>
      {/* Navigation Bar */}
      <NavigationBar setActiveSection={setActiveSection} />

      {/* Selected Date with Change Option */}
      <Box
        sx={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          marginTop: 2,
        }}
      >
        <Typography variant="body1" fontWeight="bold">
          Selected Date: {selectedDate}
        </Typography>
        <Button
          startIcon={<DateRangeIcon />}
          sx={{ marginLeft: 1, textTransform: "capitalize" }}
          onClick={handleChangeDate}
        >
          Change
        </Button>
      </Box>

      {/* Main Content */}
      <Box
        sx={{
          width: "100%",
          padding: 4,
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          minHeight: "calc(100vh - 150px)", // Adjust for header and date section
          backgroundColor: "#f5f5f5",
        }}
      >
        {renderContent()}
      </Box>
    </>
  );
}
